v-on:子组件事件="父组件事件"

  {{ total }}




Vue.component('button-counter', {
  template: '{{ counter }}',  // 点击时触发子组件事件
  data: function () {
    return {
      counter: 0
    }
  },
  methods: {
    increment: function () {
      this.counter += 1
      this.$emit('increment')  // emit向父组件传递该increment，组件上监听到increment，触发父组件的incrementTotal
    }
  },
})

new Vue({
  el: '#counter-event-example',
  data: {
    total: 0
  },
  methods: {
    incrementTotal: function () {
      this.total += 1
    }
  }
})

